void render_image(char *data);
void translate_image(char screen[32][128])
{
    // init loop values
    int i, j, k;
    int placement_index = 0;
    // iterating over the 4 segments of the column
    for (i = 0; i < 4; i++)
    {
        // control segments added
        int index_start, index_end;
        if (i == 0)
        {
            index_start = 0;
            index_end = 7;
        }
        else if (i == 1)
        {
            index_start = 8;
            index_end = 15;
        }
        else if (i == 2)
        {
            index_start = 16;
            index_end = 23;
        }
        else if (i == 3)
        {
            index_start = 24;
            index_end = 31;
        }

        // iterating over columns
        for (j = 0; j < 128; j++)
        {

            int n = 0;
            for (k = index_start; k < index_end; k++)
            {
                // clear bit if zero
                if (screen[k][j] == (char)0)
                {
                    picture[placement_index] &= ~((char)1 << n);
                }
                // set bit if 1
                if (screen[k][j] == (char)1)
                {
                    picture[placement_index] |= (char)1 << n;
                }
                n++;
            }

            placement_index++;
        }
    }
}