void addToScreen(char toAdd[32][128])
{
    int i, j;
    for (i = 0; i < 32; i++)
    {
        for (j = 0; j < 128; j++)
        {
            if (toAdd[i][j] == 1)
            {
                Screen[i][j] = 1;
            }
        }
    }
}

void removeFromScreen(char toRemove[32][128])
{
    int i, j;
    for (i = 0; i < 32; i++)
    {
        for (j = 0; j < 128; j++)
        {
            if (toRemove[i][j] == 1)
            {
                Screen[i][j] = 0;
            }
        }
    }
}

void update_screen()
{
    translate_image(Screen);
    render_image(DrawingScreen);
}

void clearScreen()
{
    int i, j;
    for (i = 0; i < 32; i++)
    {
        for (j = 0; j < 128; j++)
        {
            Screen[i][j] = 0;
        }
    }
}

void addHistogramBar(int place, int num)
{
    /*
    Place is 0-4
    0 <|  4-7
    1 |> 10-13
    2 || 16-19
    3 <  22-25
    4 >  28-31

    num is 1+ (17+ is drawn the same)
    num is adjusted to 0+
    */
    num = num - 1;
    if (num > 15)
    {
        num = 15;
        int i, j;
        for (i = (4 + (6 * place)); i < (8 + (6 * place)); i++)
        {
            for (j = 98; j < 100; j++)
            {
                Screen[i][j] = 1;
            }
        }
    }
    if (num < 0)
    {
        return;
    }
    //(4 + (6 * place)); i < (8 + (6 * place))
    int i, j;
    for (i = (4 + (6 * place)); i < (8 + (6 * place)); i++)
    {
        for (j = 5; j < (8 + (6 * num)); j++)
        {
            Screen[i][j] = 1;
        }
    }
}

