// translate into format render_image can understand
void translate_image(char toTranslate[32][128])
{
    // init loop values
    int i, j, k;
    int placement_index = 0;
    // iterating over the 4 segments of the column
    for (i = 0; i < 4; i++)
    {
        // control segments added
        int index_start, index_end;
        if (i == 0)
        {
            index_start = 0;
            index_end = 7;
        }
        else if (i == 1)
        {
            index_start = 8;
            index_end = 15;
        }
        else if (i == 2)
        {
            index_start = 16;
            index_end = 23;
        }
        else if (i == 3)
        {
            index_start = 24;
            index_end = 31;
        }

        // iterating over columns
        for (j = 0; j < 128; j++)
        {

            int n = 0;
            for (k = index_start; k < index_end; k++)
            {
                // clear bit if zero
                if (toTranslate[k][j] == (char)0)
                {
                    DrawingScreen[placement_index] &= ~((char)1 << n);
                }
                // set bit if 1
                if (toTranslate[k][j] == (char)1)
                {
                    DrawingScreen[placement_index] |= (char)1 << n;
                }
                n++;
            }

            placement_index++;
        }
    }
}

int getsw()
{
    int returnValue = PORTD;
    //               4321
    returnValue &= 0b111100000000;
    returnValue = returnValue >> 8;

    // returning status on SW1-SW4
    // return is 4321
    return returnValue;
}

int getbtns()
{
    int returnValue = PORTD;
    // btns          432
    returnValue &= 0b11100000;
    returnValue = returnValue >> 5;

    int btn1 = PORTF;
    // btn    1
    btn1 &= 0b10;
    btn1 = btn1 >> 1;

    // returning status on B1-B4
    // return is 4321
    returnValue == returnValue << 1;
    returnValue += btn1;
    return returnValue;
}

void ChangeScreen(enum ScreenType ChangeTo)
{
    if (ChangeTo == MainMenu)
    {
        clearScreen();
        CurrentScreen = MainMenu;
        addToScreen(MainScreen);
    }
    else if (ChangeTo == Bike)
    {
        clearScreen();
        CurrentScreen = Bike;
    }
    else if (ChangeTo == Sensitivity)
    {
        clearScreen();
        CurrentScreen = Sensitivity;
    }
    else if (ChangeTo == Histogram)
    {
        clearScreen();
        CurrentScreen = Histogram;
        addToScreen(HistogramScreen);
    }
    else
    {
        ChangeScreen(MainMenu);
    }
}

void doScreenStuff()
{
    int btns = getbtns();
    int btn1 = btns & 0b0001;
    int btn2 = (btns & 0b0010) >> 1;
    int btn3 = (btns & 0b0100) >> 2;
    int btn4 = (btns & 0b1000) >> 3;

    if (CurrentScreen == MainMenu)
    {
        if (btn1 == 1)
        {
            ChangeScreen(Bike);
        }
        if (btn2 == 1)
        {
            ChangeScreen(Sensitivity);
        }
        if (btn3 == 1)
        {
            ChangeScreen(Histogram);
        }
    }
    else if (CurrentScreen == Bike)
    {
        if (btn4 == 1)
        {
            if (ToggleActiveLeftManualArrow == 1)
            {
                ToggleActiveLeftManualArrow = 0;
            }
            else
            {
                ToggleActiveLeftManualArrow = 1;
            }
        }
        if (btn3 == 1)
        {
            if (ToggleActiveRightManualArrow == 1)
            {
                ToggleActiveRightManualArrow = 0;
            }
            else
            {
                ToggleActiveRightManualArrow = 1;
            }
        }
        if (btn2 == 1)
        {
            if (ActiveBrakeLight == 1)
            {
                ActiveBrakeLight = 0;
                removeFromScreen(BrakeLight);
            }
            else
            {
                ActiveBrakeLight = 1;
                addToScreen(BrakeLight);
            }
            update_screen();
        }
        if (btn1 == 1)
        {
            ChangeScreen(MainMenu);
        }
    }
    else if (CurrentScreen == Sensitivity)
    {
    }
    else if (CurrentScreen == Histogram)
    {
    }
    else
    {
        ChangeScreen(MainMenu);
    }
}

